import React, { Component } from 'react'
import Mov from './mov'
export default class Movies extends Component {
    state={
      data:[]
    }

    componentWillMount() {
      fetch(' https://api.themoviedb.org/3/search/movie?query=marvel&api_key=467b99190762c525e0407fc3f52d654c')
      .then(res => res.json())
      .then(data => this.setState({data:data.results}))
    }

    render() {
        const {data} = this.state
      return (
        <>

        {
