import React, { Component } from 'react'
import './mov.css'
import Movies from './comps/movies'
import Nav from './comps/nav'
import Mov from './comps/mov'
export default class App extends Component {
  constructor(props){
    super(props)
    this.state={
      data : [],
      search : ' ',
      urls: ''
    }
    this.getMovies = this.getMovies()
  }


  getMovies = (query='marvel') => {
    fetch(`https://api.themoviedb.org/3/search/movie?query=${query}&api_key=467b99190762c525e0407fc3f52d654c`)
    .then(res => res.json())
    .then(data => {
      console.log(data)
      this.setState({data:data.results})
    })
    .catch(e => console.log(e))
  }

  searchBar(event){
    console.log(event)
    console.log(this.getMovies())
  }  


  componentDidMount() {
   this.getMovies()
  }


  render() {
    return (
      <>
      {/* <Mov /> */}
      <Nav searchMovies={this.searchBar}/>
      {(this.state.data.length > 0) ? <Movies movies={this.state.data} /> : null}
      </>
    )
  }
}
