import React, { Component } from 'react'
import './mov.css'
import Movies from './comps/movies'
import Nav from './comps/nav'
import Mov from './comps/mov'
export default class App extends Component {

  state={
    data : [],
    search : ' ',
    urls: ''
  }
  searchBar(event){
    this.setState({
    search: event.target.value
  })
  }  

  getMovies = (query='marvel') => {
    fetch('https://api.themoviedb.org/3/search/movie?query=marvel&api_key=467b99190762c525e0407fc3f52d654c')
    .then(res => res.json())
    .then(data => {
      console.log(data)
      this.setState({data:data.results})
    })
    .catch(e => console.log(e))
  }
  componentDidMount() {
   this.getMovies()
  }


  render() {
    return (
      <>
      {/* <Mov /> */}
      <Nav />
      <Movies/>
      </>
    )
  }
}
