import Mov from './mov'
import {Row} from 'react-bootstrap'
export default class Movies extends Component {
    state={
      data:[]
    }

    searchHandler = (text )=>{
      fetch('https://api.themoviedb.org/3/search/movie?query=marvel&api_key=467b99190762c525e0407fc3f52d654c')
      .then(res => res.json())
      .then(data => this.setState({data:data.results}))
      .catch(e => console.log(e))
    }

    componentDidMount() {
      fetch('https://api.themoviedb.org/3/search/movie?query=marvel&api_key=467b99190762c525e0407fc3f52d654c')
      .then(res => res.json())
      .then(data => this.setState({data:data.results}))
      .catch(e => console.log(e))
    }

    render() {
        const {data} = this.state
        console.log(data)
        console.log(data.length)
      return (
        <>

        {
          data.map(x=>(
              <Mov key={x.id} movImg={x.poster_path} moveName={x.title}/>
          ))

        }

        </>
      )
    }
  }
